<script>
import gsap from 'gsap'
export default {
  mounted() {
    this.vueAnimation()
    this.nuxtAnimation()
    this.laravelAnimation()
    this.vuetifyAnimation()
  },
  methods: {
    vuetifyAnimation() {
      const tl = gsap.timeline({
        scrollTrigger: {
          trigger: '#vuetify-main',
          start: 'bottom bottom',
        },
      })
      tl.fromTo(
        '#vuetify-top-arrow',
        {
          transform: 'translateY(-20px)',
          opacity: 0,
        },
        {
          transform: 'translateY(0)',
          opacity: 1,
          duration: 2,
          delay: 0.5,
          ease: 'ease',
        }
      )
      gsap.fromTo(
        '#vuetify-bottom-arrow',
        {
          transform: 'translateY(-60px)',
          opacity: 0,
        },
        {
          transform: 'translateY(0)',
          opacity: 1,
          duration: 3,
          delay: 0.5,
          ease: 'ease',
        }
      )
    },
    laravelAnimation() {
      const tl = gsap.timeline({
        scrollTrigger: {
          trigger: '#laravel-main',
          start: 'bottom bottom',
        },
      })
      tl.fromTo(
        '#laravel-main',
        {
          strokeDasharray: 1150,
          strokeDashoffset: 1150,
          fill: 'transparent',
          stroke: 'rgb(255, 45, 32)',
          strokeWidth: '1px',
        },
        {
          strokeDashoffset: 0,
          duration: 1,
          ease: 'ease',
          delay: 0.5,
        }
      )
      tl.fromTo(
        '#laravel-main',
        {
          fill: 'transparent',
        },
        {
          fill: 'rgb(255, 45, 32)',
          duration: 1,
          ease: 'ease',
        },
        '<'
      )
    },
    nuxtAnimation() {
      const tl = gsap.timeline({
        scrollTrigger: {
          trigger: '#nuxt-main',
          start: 'bottom bottom',
        },
      })
      tl.fromTo(
        '#nuxt-left-triangle',
        {
          transform: 'translateX(40px)',
          opacity: 0,
        },
        {
          transform: 'translateX(0)',
          opacity: 1,
          duration: 1.5,
          delay: 0.5,
          ease: 'ease',
        },
        '<'
      )
      tl.fromTo(
        '#nuxt-right-triangle',
        {
          transform: 'translateX(-40px)',
          opacity: 0,
        },
        {
          transform: 'translateX(0)',
          opacity: 1,
          duration: 1.5,
          ease: 'ease',
        },
        '<'
      )
      tl.fromTo(
        '#nuxt-middle-triangle',
        {
          transform: 'translateY(-40px)',
          opacity: 0,
        },
        {
          transform: 'translateY(0)',
          opacity: 1,
          duration: 1.5,
          ease: 'ease',
        },
        '<'
      )
    },
    vueAnimation() {
      const tl = gsap.timeline({
        scrollTrigger: {
          trigger: '#vue-main',
          start: 'bottom bottom',
        },
      })
      tl.fromTo(
        '#vue-green-part',
        {
          strokeDasharray: 1000,
          strokeDashoffset: 1000,
          stroke: 'rgb(65, 184, 131)',
          strokeWidth: 4,
          fill: 'transparent',
        },
        {
          strokeDashoffset: 0,
          duration: 1,
          delay: 0.5,
          ease: 'ease',
        }
      )
      tl.fromTo(
        '#vue-blue-part',
        {
          strokeDasharray: 1000,
          strokeDashoffset: 1000,
          stroke: 'rgb(53, 73, 94)',
          strokeWidth: 4,
          fill: 'transparent',
        },
        {
          strokeDashoffset: 0,
          duration: 1,
          ease: 'ease',
        },
        '<'
      )
      tl.fromTo(
        '#vue-green-part',
        {
          fill: 'transparent',
        },
        {
          fill: 'rgb(65, 184, 131)',
          duration: 0.5,
          ease: 'ease',
        }
      )
      tl.fromTo(
        '#vue-blue-part',
        {
          fill: 'transparent',
        },
        {
          fill: 'rgb(53, 73, 94)',
          duration: 0.5,
          ease: 'ease',
        },
        '<'
      )
    },
  },
}
